mapper.select:
select

from trans_order


    and prepare_order_id = #{prepareOrderId,jdbcType=INTEGER}


    and event_id = #{eventId,jdbcType=INTEGER}


    and huo_order_id = #{huoOrderId,jdbcType=VARCHAR}


    and  huo_symbol = #{huoSymbol,jdbcType=VARCHAR}


    and  huo_type = #{huoType,jdbcType=VARCHAR}


    and  huo_amount = #{huoAmount,jdbcType=VARCHAR}


    and  huo_price = #{huoPrice,jdbcType=VARCHAR}


    and  huo_state = #{huoState,jdbcType=VARCHAR}


    and huo_created_time = #{huoCreatedTime,jdbcType=TIMESTAMP}


    and huo_canceled_time = #{huoCanceledTime,jdbcType=TIMESTAMP}


    and  huo_finished_time = #{huoFinishedTime,jdbcType=TIMESTAMP}


    and  huo_filled_cash_amount = #{huoFilledCashAmount,jdbcType=VARCHAR}


    and  huo_filled_amount = #{huoFilledAmount,jdbcType=VARCHAR}


    and huo_filled_fees = #{huoFilledFees,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}
